DECLARE @DateTimeFormat as varchar(20);
DECLARE @localdatetime as datetime;
Set @DateTimeFormat ='@{item().DateTimeFormatInFileName}';
EXEC @localdatetime = [dbo].[udf_getLocalDTM];
select FORMAT(@localdatetime, Case when len(@DateTimeFormat)=0 then'yyyy_MM_dd_HHmmss' else @DateTimeFormat end) as  localstarttime;

